import React, { useState, useEffect } from 'react';
function App() {
  const [todos, setTodos] = useState([]);
  const [text, setText] = useState('');
  // Load from localStorage or fetch sample todos
  useEffect(() => {
    const saved = localStorage.getItem('todos_v1');
    if (saved) {
      setTodos(JSON.parse(saved));
    } else {
      fetch('https://jsonplaceholder.typicode.com/todos?_limit=5')
        .then(res => res.json())
        .then(data => {
          const list = data.map(t => ({ id: t.id, title: t.title, completed: t.completed }));
          setTodos(list);
          localStorage.setItem('todos_v1', JSON.stringify(list));
        });
    }
  }, []);
  useEffect(() => {
    localStorage.setItem('todos_v1', JSON.stringify(todos));
  }, [todos]);
  function addTodo() {
    if (!text.trim()) return;
    const newTodo = { id: Date.now(), title: text.trim(), completed: false };
    setTodos([newTodo, ...todos]);
    setText('');
  }
  function toggle(id) {
    setTodos(todos.map(t => t.id === id ? { ...t, completed: !t.completed } : t));
  }
  function remove(id) {
    setTodos(todos.filter(t => t.id !== id));
  }
  return (
    <div className="container">
      <h1>TODO App</h1>
      <div className="input-row">
        <input value={text} onChange={e => setText(e.target.value)} placeholder="Add todo..." />
        <button onClick={addTodo}>Add</button>
      </div>
      <ul className="todo-list">
        {todos.map(t => (
          <li key={t.id} className={t.completed ? 'done' : ''}>
            <span onClick={() => toggle(t.id)}>{t.title}</span>
            <button className="del" onClick={() => remove(t.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
}
export default App;
